	/**
	 * @covers Fuel\Common\Arr::sort
	 * @group Common
	 */
	public function testSort()
	{
		$result = Arr::sort(array(), 'info.pet.type');
		$this->assertEquals(array(), $result);

		$input = array(
			array(
				'info' => array(
					'pet' => array(
						'type' => 'dog'
					)
				),
			),
			array(
				'info' => array(
					'pet' => array(
						'type' => 'fish'
					)
				),
			),
			array(
				'info' => array(
					'pet' => array(
						'type' => 'cat'
					)
				),
			),
		);

		$expected = array(
			array(
				'info' => array(
					'pet' => array(
						'type' => 'cat'
					)
				),
			),
			array(
				'info' => array(
					'pet' => array(
						'type' => 'dog'
					)
				),
			),
			array(
				'info' => array(
					'pet' => array(
						'type' => 'fish'
					)
				),
			),
		);

		$result = Arr::sort($input, 'info.pet.type');
		$this->assertEquals($expected, $result);

		$expected = array(
			array(
				'info' => array(
					'pet' => array(
						'type' => 'fish'
					)
				),
			),
			array(
				'info' => array(
					'pet' => array(
						'type' => 'dog'
					)
				),
			),
			array(
				'info' => array(
					'pet' => array(
						'type' => 'cat'
					)
				),
			),
		);

		$result = Arr::sort($input, 'info.pet.type', 'desc');
		$this->assertEquals($expected, $result);
	}


